const unsigned short* fontVerdana22x28 = &Verdana22x28[0];

uint8_t X_font=22, Y_font=28, Bytes_font;
uint16_t Start_font = 0x20;

void PutStringRus (uint8_t ix, uint8_t iy, char* iString, uint16_t fc, uint16_t bc)
{
	volatile uint8_t xpos;
	volatile uint8_t sm, snum;    
	
     xpos = ix;
	
	if (Y_font%8) 
		Bytes_font = (Y_font/8) + 1;
	else
		Bytes_font = (Y_font/8);
		
    for(int sym = 0; iString[sym]; ++sym)
    {
         sm = iString[sym];
         snum = iString[sym];
				 snum = sm-Start_font;//Sdvig setki na na4alo tablicu
			
			const unsigned short* symbol = &(fontVerdana22x28)[(1+X_font*Bytes_font)*(snum)];
			
			
        for(uint8_t x = 0; x<symbol[0]; ++x)
        {
            for(uint8_t y = 0; y<Y_font; ++y)
            {
                if(symbol[1+y/8+x*Bytes_font] & (0x01<<(y%8)))
                    Gui_DrawPoint(xpos+x,iy+y,fc);
                else
                    Gui_DrawPoint(xpos+x,iy+y,bc);
            }
        }
        xpos += symbol[0];
    }
}



