const unsigned short* fontVerdana25x34 = &Verdana25x34[0];

uint8_t X_font=25, Y_font=34, Bytes_font;
uint16_t Start_font = 0x20, Additional_start = 11;

void PutStringRus (uint8_t ix, uint8_t iy, char* iString, uint16_t fc, uint16_t bc)
{
	volatile uint8_t xpos;
	volatile uint8_t sm, snum;    
	
     xpos = ix;
	
	if (Y_font%8) 
		Bytes_font = (Y_font/8) + 1;
	else
		Bytes_font = (Y_font/8);
		
    for(int sym = 0; iString[sym]; ++sym)
    {
         sm = iString[sym];
        // snum = iString[sym];
				 snum = sm-Start_font - Additional_start;//Sdvig setki na na4alo tablicu
			
			const unsigned short* symbol = &(fontVerdana25x34)[(1+X_font*Bytes_font)*(snum)];
			
			
        for(uint8_t x = 0; x<symbol[0]; ++x)
        {
            for(uint8_t y = 0; y<Y_font; ++y)
            {
                if(symbol[1+y/8+x*Bytes_font] & (0x01<<(y%8)))
                    Gui_DrawPoint(xpos+x,iy+y,fc);
                else
                    Gui_DrawPoint(xpos+x,iy+y,bc);
            }
        }
        xpos += symbol[0];
    }
}


void Colorized (void)
{uint16_t count=0;
	for(uint8_t i=0; i<128; i++)
		for(uint8_t j=0; j<128; j++)
			{
				count += 1;
				Gui_DrawPoint(i,j,(((i/4)<<10) & 0xF800) | (((j/4)) & 0x1F) | ((((j + i)/4)<<5) & 0x07E0));
			}
}

